import React, { useState } from "react";
import Checkbox from "../Checkbox/Checkbox";
import Dropdown from "../Dropdown/Dropdown";
import Input from "../Input/Input";

import './group.css';

const Group = (props) => {
    const [isOpened, toggleOpen] = useState(false)

    const toggleButton = () => {
        toggleOpen((lastOpenedState) => !lastOpenedState);
    }

    const renderData = (currentElement) => {
        {
            const {data_type, label, value, uid, _metadata = {}} = currentElement;
            if (data_type === 'group') {
                return (<Group key={uid} data={currentElement.value}/>)
            } else if (data_type === 'boolean') {
                return (<Checkbox value={value} uid={uid} label={label} isRequired={_metadata.required} />)
            } else if (data_type === 'dropdown'){
                return (<Dropdown value={value} uid={uid} label={label} isRequired={_metadata.required} />)
            }
            return (
                <Input
                    key={uid}
                    type={data_type}
                    label={label}
                    value={value}
                    uid={uid}
                    isRequired={_metadata.required}
                />
            );
        }
    }

    return (
        <div className="group-container container">
            <div className={`group-accordian ${isOpened ? 'opened' : 'closed'}`}>
                <span>Group</span>
                <button className="accordian-button" type="button" onClick={toggleButton}>
                    {isOpened ? <React.Fragment>&#x25B2;</React.Fragment> : <React.Fragment>&#x25BC;</React.Fragment>}
                </button>
            </div>
            {isOpened && (
                <div className="group-fields-subcontainer">
                    {props.data.map(renderData)}
                </div>
            )
            }
        </div>
    )
};

export default Group;
