import React, { useState } from 'react'
import Label from '../Label/Label';

import './checkbox.css';

const Checkbox = (props) => {

    const [value, updateValue] = useState(props.value);

    const onValueChange = (event) => {
        updateValue(event.target.value);
    }

    return (
        <div className={`checkbox-wrapper container ${props.errors?.[props.uid]? 'error-state ': 'success-state'}`}>
            <input
                type="checkbox"
                id={props.uid} 
                name={props.uid} 
                className="input-component form-field" 
                onChange={onValueChange} 
                value={value}
                placeholder={props.placeholder}
                {...props.register(props.uid)}
            />
            <Label for={props.uid} label={props.label} isRequired={props.isRequired} />
        </div>
    )
};

export default Checkbox;
